mapper.select:
SELECT tlal.ip, COUNT(tlal.ip) AS count
FROM t_link tl
         INNER JOIN
     t_link_access_logs tlal ON tl.full_short_url = tlal.full_short_url
WHERE tlal.full_short_url = #{param.fullShortUrl}
  AND tl.gid = #{param.gid}
  AND tl.del_flag = '0'
  AND tl.enable_status = #{param.enableStatus}
  AND tlal.create_time BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY tlal.full_short_url, tl.gid, tlal.ip
ORDER BY count DESC
LIMIT 5;

SELECT tlal.user, CASE WHEN MIN(tlal.create_time) BETWEEN #{startDate} AND #{endDate} THEN '新访客' ELSE '老访客' END AS uvType FROM t_link tl INNER JOIN t_link_access_logs tlal ON tl.full_short_url = tlal.full_short_url WHERE tlal.full_short_url = #{fullShortUrl} AND tl.gid = #{gid} AND tl.del_flag = '0' AND tl.enable_status = #{enableStatus} AND tlal.user IN
#{item}
GROUP BY tlal.user;

SELECT tlal.user, CASE WHEN MIN(tlal.create_time) BETWEEN #{startDate} AND #{endDate} THEN '新访客' ELSE '老访客' END AS uvType FROM t_link tl INNER JOIN t_link_access_logs tlal ON tl.full_short_url = tlal.full_short_url WHERE tl.gid = #{gid} AND tl.del_flag = '0' AND tl.enable_status = '0' AND tlal.user IN
#{item}
GROUP BY tlal.user;

SELECT COUNT(tlal.user)          AS pv,
       COUNT(DISTINCT tlal.user) AS uv,
       COUNT(DISTINCT tlal.ip)   AS uip
FROM t_link tl
         INNER JOIN
     t_link_access_logs tlal ON tl.full_short_url = tlal.full_short_url
WHERE tlal.full_short_url = #{param.fullShortUrl}
  AND tl.gid = #{param.gid}
  AND tl.del_flag = '0'
  AND tl.enable_status = #{param.enableStatus}
  AND tlal.create_time BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY tlal.full_short_url, tl.gid;
