mapper.insert:
INSERT INTO t_link_browser_stats (full_short_url, date, gid, cnt, browser, create_time, update_time, del_flag)
VALUES ( #{stats.fullShortUrl}, #{stats.gid}, #{stats.date}, #{stats.cnt}, #{stats.browser}
       , #{stats.createTime}, #{stats.updateTime}, #{stats.delFlag})
ON DUPLICATE KEY UPDATE cnt = cnt + #{stats.cnt};

mapper.select:
SELECT tlbs.browser,
       SUM(tlbs.cnt) AS count
FROM t_link tl
         INNER JOIN
     t_link_browser_stats tlbs ON tl.full_short_url = tlbs.full_short_url
WHERE tlbs.full_short_url = #{param.fullShortUrl}
  AND tl.gid = #{param.gid}
  AND tl.del_flag = '0'
  AND tl.enable_status = #{param.enableStatus}
  AND tlbs.date BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY tlbs.full_short_url, tl.gid, tlbs.browser;

SELECT tlbs.browser,
       SUM(tlbs.cnt) AS count
FROM t_link tl
         INNER JOIN
     t_link_browser_stats tlbs ON tl.full_short_url = tlbs.full_short_url
WHERE tl.gid = #{param.gid}
  AND tl.del_flag = '0'
  AND tl.enable_status = '0'
  AND tlbs.date BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY tl.gid, tlbs.browser;
