mapper.insert:
INSERT INTO t_link_device_stats (full_short_url, gid, date, cnt, device, create_time, update_time, del_flag)
VALUES ( #{stats.fullShortUrl}, #{stats.gid}, #{stats.date}
       , #{stats.cnt}, #{stats.device}, #{stats.createTime}, #{stats.updateTime}, #{stats.delFlag})
ON DUPLICATE KEY UPDATE cnt = cnt + #{stats.cnt};

mapper.select:
SELECT
    tlds.device,
    SUM(tlds.cnt) AS cnt
FROM
    t_link tl INNER JOIN
    t_link_device_stats tlds ON tl.full_short_url = tlds.full_short_url
WHERE
    tlds.full_short_url = #{param.fullShortUrl}
  AND tl.gid = #{param.gid}
  AND tl.del_flag = '0'
  AND tl.enable_status = #{param.enableStatus}
  AND tlds.date BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY
    tlds.full_short_url, tl.gid, tlds.device;

SELECT tlds.device,
       SUM(tlds.cnt) AS cnt
FROM t_link tl
         INNER JOIN
     t_link_device_stats tlds ON tl.full_short_url = tlds.full_short_url
WHERE tl.gid = #{param.gid}
  AND tl.del_flag = '0'
  AND tl.enable_status = '0'
  AND tlds.date BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY tl.gid, tlds.device;
