mapper.insert:
INSERT INTO t_link_os_stats (full_short_url, gid, date, cnt, os, create_time, update_time, del_flag)
VALUES ( #{stats.fullShortUrl}, #{stats.gid}, #{stats.date}, #{stats.cnt}, #{stats.os}
       , #{stats.createTime}, #{stats.updateTime}, #{stats.delFlag})
ON DUPLICATE KEY UPDATE cnt = cnt + #{stats.cnt};

mapper.select:
SELECT tlos.os,
       SUM(tlos.cnt) AS count
FROM t_link tl
         INNER JOIN
     t_link_os_stats tlos ON tl.full_short_url = tlos.full_short_url
WHERE tlos.full_short_url = #{param.fullShortUrl}
  AND tl.gid = #{param.gid}
  AND tl.del_flag = '0'
  AND tl.enable_status = #{param.enableStatus}
  AND tlos.date BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY tlos.full_short_url, tl.gid, tlos.os;

SELECT tlos.os,
       SUM(tlos.cnt) AS count
FROM t_link tl
         INNER JOIN
     t_link_os_stats tlos ON tl.full_short_url = tlos.full_short_url
WHERE tl.gid = #{param.gid}
  AND tl.del_flag = '0'
  AND tl.enable_status = '0'
  AND tlos.date BETWEEN #{param.startDate} and #{param.endDate}
GROUP BY tl.gid, tlos.os;
